import React, { useState } from 'react'
import { FaComments } from 'react-icons/fa'

export default function ChatBox() {
  const [isOpen, setIsOpen] = useState(false)

  const toggleChat = () => {
    setIsOpen(!isOpen)
  }

  return (
    <div className="fixed bottom-5 right-5">
      <button
        onClick={toggleChat}
        className="bg-blue-500 text-white rounded-full p-3 shadow-lg transition-transform transform hover:scale-110 animate-bounce"
      >
        <FaComments size={24} />
      </button>

      {isOpen && (
        <div className="bg-white shadow-lg rounded-lg p-4 mt-2 w-64">
          <h2 className="font-semibold">Chat with us!</h2>
          {/* Chat content goes here */}
          <div className="mt-2">
            <p>How can we help you today?</p>
            {/* Add more chat functionality here */}
          </div>
        </div>
      )}
    </div>
  )
}
